var request = require('./request');

module.exports.customerList = function (page, pageSize) {
    return request.get({
        method: 'bubugao.mobile.customer.list.get',
        page: page,
        pageSize: pageSize
    });
};

module.exports.customerShop = function (sellerId, areaId, page, pageSize) {
    return request.get({
        method: 'bubugao.mobile.customer.shop.search',
        sellerId: sellerId,
        areaId: areaId,
        page: page,
        pageSize: pageSize
    });
};

module.exports.customerAllShop = function (sellerId,page,pageSize) {
    return request.get({
        method: 'bubugao.mobile.customer.shop.search',
        sellerId: sellerId,
        page: page,
        pageSize: pageSize
    });
};