var request = require('./request');

/**
 * 交易小票列表
 * @param tokensOrUnionid
 * @param page
 * @param pageSize
 * @param beginTime
 * @param endTime
 * @returns {*}
 */
module.exports.posNum = function (tokensOrUnionid, page, pageSize, beginTime, endTime) {
    return request.getTokens(tokensOrUnionid).then(function (tokens) {
        return request.getAuth({
            method: 'bubugao.mobile.local.siebel.posnums.get',
            page: page,
            pageSize: pageSize,
            beginTime: beginTime,
            endTime: endTime
        }, tokens);
    });
};

/**
 * 交易小票详情
 * @param txnId
 * @returns {*}
 */
module.exports.posDetail = function (txnId) {
    return request.get({
        method: 'bubugao.mobile.local.siebel.pos.details',
        txnId: txnId
    });
};