var util = require('util');

var WeChatError = function (code, msg, path) {
    this.code = code;
    this.message = msg;
    this.path = path;
    this.type = 'wechat';
};
var ApiError = function (code, msg, data) {
    this.code = code;
    this.message = msg;
    this.data = data;
    this.type = 'api';
};
var SystemError = function (code, msg, path) {
    this.code = code;
    this.message = msg;
    this.path = path;
    this.type = 'system';
};

var NodeApiError = function (code, msg, path) {
    this.code = code;
    this.message = msg;
    this.path = path;
    this.type = 'nodeApi';
};

util.inherits(WeChatError, Error);
util.inherits(ApiError, Error);
util.inherits(SystemError, Error);
util.inherits(NodeApiError, Error);

module.exports.WeChatError = WeChatError;
module.exports.ApiError = ApiError;
module.exports.SystemError = SystemError;
module.exports.NodeApiError = NodeApiError;
