var apis = require('../');
var chai = require('chai');
var chaiPromise = require('chai-as-promised');
chai.use(chaiPromise);
chai.should();


describe('area', function () {
    before(function(){
        apis.config({
            api:{
                printLog:false,
                host: '10.200.51.104',//API测试服务器地址
                port: 8080,//API测试服务器端口号
                path: '/bubugao-mobile-api/app',//测试服务器普通API请求地址
                authPath: '/bubugao-mobile-api/app/auth',//测试服务器验证API请求地址
                ver: '2.7'
            }
        });
    });
    describe('#readRegion', function () {
        it('getRegionByCode', function () {
            return apis.area.getRegionByCode('430600000000',0).then(function (data) {
                //console.log(data);
                return data;
            }).fail(function (err) {
                //console.error(err);
                throw err;
            }).should.not.be.rejected;
        });

        it('location', function () {
            return apis.area.location('40.001543025655','116.33928948239').then(function (data) {
                console.log(data);
                return data;
            }).fail(function (err) {
                //console.error(err);
                throw err;
            }).should.not.be.rejected;
        });


        it('area', function () {
            return apis.area.area().then(function (data) {
                //console.log(data);
                return data;
            }).fail(function (err) {
                //console.error(err);
                throw err;
            }).should.not.be.rejected;
        });
    });
});