var apis = require('../');
var chai = require('chai');
var chaiPromise = require('chai-as-promised');
chai.use(chaiPromise);
chai.should();



describe('assistance', function () {
    before(function(){
        apis.config({
            api:{
                webHost:'jsbb.yunhou.com',//线上域名
                host: 'jsbb.yunhou.com',//API测试服务器地址
                port: 80,//API测试服务器端口号
                path: '/yunhou-wechat/app',//测试服务器普通API请求地址
                authPath: '/yunhou-wechat/app/auth',//测试服务器验证API请求地址
                ver: '2.7'
            }
        });
    });

    describe('#getIdentityCode', function () {
        it('should get code with no error', function () {
            return apis.assistance.getIdentityCode('').should.not.be.rejected;
        });
    });
    describe('#school', function () {
        it('should get school with no error', function () {
            return apis.assistance.school().should.not.be.rejected;
        });
    });
});