var apis = require('../');
var chai = require('chai');
var chaiPromise = require('chai-as-promised');
chai.use(chaiPromise);
chai.should();

describe('coupon', function () {
    before(function () {
        apis.config({
            api: {
                printLog: true,
                host: '10.200.51.104',//API测试服务器地址
                port: 8080,//API测试服务器端口号
                path: '/bubugao-mobile-api/app',//测试服务器普通API请求地址
                authPath: '/bubugao-mobile-api/app/auth',//测试服务器验证API请求地址
                ver: '2.7',
                autoToken: true,
                testTokens: {
                    tokenId: '6a9973a6da5e4a95abea10631c959d90',
                    secret: '789a7cdfad9c8f37152a554a0fbd7ab8'
                }
            }
        });
    });

    describe('#couponSiebel', function () {
        it('should get couponSiebel', function () {
            return apis.request.getTokens().then(function (tokens) {
                return apis.coupons.countAll(tokens);
            }).then(function (data) {
                console.log(data);
                return data;
            }).fail(function (err) {
                console.error(err);
                throw err;
            }).should.not.be.rejected;
        });
    });
});
