var apis = require('../');
var chai = require('chai');
var chaiPromise = require('chai-as-promised');
chai.use(chaiPromise);
chai.should();

describe('easy', function () {
    before(function () {
        apis.config({
            api: {
                printLog: true,
                printSign: false,
                host: '10.200.51.104',//API测试服务器地址
                port: 8080,//API测试服务器端口号
                path: '/bubugao-mobile-api/app',//测试服务器普通API请求地址
                authPath: '/bubugao-mobile-api/app/auth',//测试服务器验证API请求地址
                //host: 'mi.bubugao-inc.com',//线上主机地址
                //port: 80,//线上主机端口
                //path: '/yunhou-mi/app',//线上普通API请求地址
                //authPath: '/yunhou-mi/app/auth',//线上验证API请求地址
                ver: '2.7',
                autoToken: false,
                timeout:600000,
                testTokens: {
                    tokenId: '',
                    secret: ''
                }
            },
            weixin: {
                printLog: false
            },
            easy:{
                host:'116.236.251.158',
                port:1318,
                printLog: true
            }
        });
    });

    describe('#token', function () {
        it('should get token', function () {
            return apis.easy.easy_token('oB8iWjqr03cjTpmfyUFml7KL9P5x').then(function (data) {
                console.log(data);
                return data;
            }).fail(function (err) {
                console.error(err);
                throw err;
            }).should.not.be.rejected;
        });
    });
});

